echo("Read trees from file now:")
let f = open("test_data/phylo.tre")
var i = 0
for tree in readtree(f):
  echo("\n--------------------")
  echo(i ," Next tree:", tree.newick[0 .. 10], "...")
  tree.summary()
  if i == 6: #assert test for large tree using data created with ape
    echo "Some additional testing for large phylogeny..."
    echo "Checking if edge_lengths are ape compatible..."
    for j in 0 .. len(tree.edge_length)-1:
      if tree.edge_length[j].contains('E'): # number with scientific notation (very short branch length): skip as ape converts this to float. there is only a single value here!
        continue
      if len(tree.edge_length[j]) < 8:
        assert tree.edge_length[j] == edge_length[j]
      else: # since ape rounds the numbers, they are not identical. Therefore in this case we only take the first 8 characters
        assert tree.edge_length[j][0 .. 7] == edge_length[j][0 .. 7]
    echo "Checking if node_label are ape compatible..."
    assert tree.node_label == node_label
    echo "Checking if edge (tree structure) are ape compatible..."
    assert tree.edge == edge
    echo "Checking if tip_label are ape compatible..."
    assert tree.tip_label == tip_label
    tree.ape_edges()
  i.inc()
  echo("----------------\n")
